import { Component, OnInit } from '@angular/core';
import { Hero } from './hero';
import { HeroService } from './hero.service';

@Component({
  selector: 'app-heroes',
  templateUrl: 'app/heroes.component.html',
  styleUrls: ['./heroes.component.css']
})
export class HeroesComponent implements OnInit {
  addingHero = false;
  heroes: any = [];
  selectedHero: Hero = new Hero;

  constructor(private heroService: HeroService) {}

  ngOnInit() {
   this.getHeroes();
  }

  cancel() {
    this.addingHero = false;
    this.selectedHero = new Hero();
  }

  deleteHero(hero: Hero) {
    this.heroService.deleteHero(hero).subscribe((res: any) => {
      this.heroes = this.heroes.filter((h: Hero) => h !== hero);
      if (this.selectedHero === hero) {
        this.selectedHero = new Hero();
      }
    });
  }

  getHeroes() {
    return this.heroService.getHeroes().subscribe((heroes: any) => {
      this.heroes = heroes;
    });
  }

  enableAddMode() {
    this.addingHero = true;
    this.selectedHero = new Hero();
  }

  onSelect(hero: Hero) {
    this.addingHero = false;
    this.selectedHero = hero;
  }

  save() {
    if (this.addingHero) {
      this.heroService.addHero(this.selectedHero).subscribe((hero: any) => {
        this.addingHero = false;
        this.selectedHero = new Hero();
        this.heroes.push(hero);
      });
    } else {
      this.heroService.updateHero(this.selectedHero).subscribe((hero: any) => {
        this.addingHero = false;
        this.selectedHero = new Hero();
      });
    }
  }
}
